package ThreePM1029;

public class JunGamExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int opr = 0;
		opr +=3;
		System.out.println(opr++);
		System.out.println(opr);
		System.out.println(++opr);
		System.out.println(opr);
		System.out.println(opr--);
		System.out.println(opr);
		System.out.println(--opr);
		System.out.println(opr);
	}

}
